    public int[][] restoreMatrix(int[] rowSum, int[] colSum) {
        int[][] result = new int[rowSum.length][colSum.length];

        int curColSum = 0;
        for (int i = 0; i < rowSum.length; i++) {
            result[i][0] = rowSum[i];
            curColSum += rowSum[i];
        }

        int temp = 0;
        for (int i = 0; i < colSum.length - 1; i++) {
            for (int j = 0; j < rowSum.length + 1; j++) {
                if (curColSum == colSum[i]) {
                    curColSum = temp;
                    temp = 0;
                    break;
                } else {
                    int diff = (curColSum - colSum[i]);
                    int planRemove = Math.min(diff, result[j][i]);
                    curColSum -= planRemove;
                    result[j][i] = result[j][i] - planRemove;
                    temp += planRemove;
                    result[j][i + 1] = planRemove;
                }
            }
        }
        return result;
    }

