    public int[][] restoreMatrixv2(int[] rowSum, int[] colSum) {
        int[][] result = new int[rowSum.length][colSum.length];

        BigDecimal curColSum = BigDecimal.valueOf(0);
        for (int i = 0; i < rowSum.length; i++) {
            result[i][0] = rowSum[i];
            curColSum = curColSum.add(BigDecimal.valueOf(rowSum[i]));
        }

        BigDecimal temp = BigDecimal.valueOf(0);
        for (int i = 0; i < colSum.length - 1; i++) {
            for (int j = 0; j < rowSum.length + 1; j++) {
                if (curColSum.compareTo(BigDecimal.valueOf(colSum[i])) == 0) {
                    curColSum = temp;
                    temp = BigDecimal.valueOf(0);
                    break;
                } else {
                    BigDecimal diff = curColSum.add(BigDecimal.valueOf(-colSum[i]));
                    int planRemove = 0;
                    if (diff.compareTo(BigDecimal.valueOf(result[j][i])) < 0) {
                        planRemove = diff.intValue();
                    } else {
                        planRemove = result[j][i];
                    }
                    curColSum = curColSum.add(BigDecimal.valueOf(-planRemove));
                    result[j][i] = result[j][i] - planRemove;
                    temp = temp.add(BigDecimal.valueOf(planRemove));
                    result[j][i + 1] = planRemove;
                }
            }
        }
        return result;
    }

